mapper.select:
SELECT a.id uuid, a.title, a.url, a.openDate, ifnull(a.province,'') province, ifnull(a.city,'') city, ifnull(a.county,'') county, ifnull(`type`,0) `type`, ifnull(rank,0) rank, ifnull(redisId,0) redisId, ifnull(websitePlanId,0) websitePlanId, ifnull(tableName,'') tableName, b.content, a.otherType, a.uuid snatchNumber, a.biddingType, a.businessType, a.edit edit, a.source FROM ${snatchurlTable} AS a LEFT JOIN ${snatchurlContentTable} AS b ON a.id = b.snatchUrlId
a.isShow = 0 AND a.type = #{type} AND a.openDate BETWEEN DATE_SUB(#{openDate},INTERVAL 3 DAY) AND DATE_SUB(#{openDate},INTERVAL -3 DAY) AND a.url NOT LIKE CONCAT('%',#{url},'%')
AND a.city = #{city}
AND a.title LIKE #{title}

mapper.update:
UPDATE FROM ${snatchurlTable}
SET url = #{url}, title = #{title}, openDate = #{openDate}, province = #{province}, city = #{city},
county = #{county}, rank = #{rank}, websitePlanId = #{websitePlanId}, uuid = #{uuid}, businessType = #{businessType},
changeNum = changeNum + 1, otherType = #{otherType}, snatchDateTime = NOW(), redisId = #{redisId}, source = #{source}
WHERE id = #{id}
